mapper.select:
		select `Labor_ID`,`Budget_ID`,`Employee_ID`,`Labor_Begin_Time`,`Labor_End_Time`,
		`Labor_Total_Day_Num`,`Labor_Rate`,`Labor_Total_Hour_Num`,`Labor_Total_Cost`,
		`Labor_Remarks`,`Labor_Operator`,`Labor_Operate_TIme`,`Labor_Grand_Total_Cost` from budget_labor

		  		   and Labor_ID = #{laborId} 
		  		   and Budget_ID = #{budgetId} 
		  		   and Employee_ID = #{employeeId} 
		  		   and Labor_Begin_Time = #{laborBeginTime} 
		  		   and Labor_End_Time = #{laborEndTime} 
		  		   and Labor_Total_Day_Num = #{laborTotalDayNum} 
		  		   and Labor_Rate = #{laborRate} 
		  		   and Labor_Total_Hour_Num = #{laborTotalHourNum} 
		  		   and Labor_Total_Cost = #{laborTotalCost} 
		  		   and Labor_Remarks = #{laborRemarks} 
		  		   and Labor_Operator = #{laborOperator} 
		  		   and Labor_Operate_TIme = #{laborOperateTime} 
		  		   and Labor_Grand_Total_Cost = #{laborGrandTotalCost} 



                order by ${sort} ${order}


                order by Labor_ID desc



			limit #{offset}, #{limit}

select count(*) from budget_labor

  		   and Labor_ID = #{laborId} 
  		   and Budget_ID = #{budgetId} 
