mapper.select:
		select a.`Employee_ID`,a.`Dept_ID`,`Job_ID`,`Inner_User_ID`,`User_Role_ID`,`Module_ID`,
		`Employee_Name`,`Employee_Dept`,`Employee_Center`,
		`Employee_Join_Date`,`Employee_Salary_Hour`,`Employee_Status`,`Employee_Phone_Number`,
		`Employee_Inner_Phone_Number`,`Employee_QQ`,`Employee_Remarks`,
		b.name Employee_Operator_Name,`Employee_Operate_Time`,
		s.role_name as userRoleName,b.name as employeeOperatorName
		from inner_org_employee a
		left JOIN sys_user b ON a.Employee_Operator=b.user_id
		left join sys_role s on a.User_Role_ID = s.role_id

		  		   and a.Dept_ID = #{deptId} 
		  		   and Job_ID = #{jobId} 
		  		   and User_Role_ID = #{userRoleId} 
		  		   and Module_ID = #{moduleId} 
		  		   and Inner_User_ID = #{innerUserId} 
		  		   and Employee_Center = #{employeeCenter} 
		  		   and Employee_Join_Date = #{employeeJoinDate} 
		  		   and Employee_Salary_Hour = #{employeeSalaryHour} 
		  		   and Employee_Status = #{employeeStatus} 
		  		   and Employee_Phone_Number = #{employeePhoneNumber} 
		  		   and Employee_Inner_Phone_Number = #{employeeInnerPhoneNumber} 
		  		   and Employee_QQ = #{employeeQq} 
		  		   and Employee_Remarks = #{employeeRemarks} 
		  		   and Employee_Operate_Time = #{employeeOperateTime} 

		  		   and Employee_ID = #{employeeId} 
		  		   and Employee_Name like #{employeeName} 
		  		   and Employee_Dept like #{employeeDept} 
		  		   and b.name like #{employeeOperatorName} 



                order by ${sort} ${order}


                order by Employee_ID desc



			limit #{offset}, #{limit}

select count(*) from inner_org_employee a
left JOIN sys_user b ON a.Employee_Operator=b.user_id
left join sys_role s on a.User_Role_ID = s.role_id

  		   and Dept_ID = #{deptId} 
  		   and Job_ID = #{jobId} 
  		   and User_Role_ID = #{userRoleId} 
  		   and Module_ID = #{moduleId} 
  		   and Inner_User_ID = #{innerUserId} 
  		   and Employee_Center = #{employeeCenter} 
  		   and Employee_Join_Date = #{employeeJoinDate} 
  		   and Employee_Salary_Hour = #{employeeSalaryHour} 
  		   and Employee_Status = #{employeeStatus} 
  		   and Employee_Phone_Number = #{employeePhoneNumber} 
  		   and Employee_Inner_Phone_Number = #{employeeInnerPhoneNumber} 
  		   and Employee_QQ = #{employeeQq} 
  		   and Employee_Remarks = #{employeeRemarks} 
  		   and Employee_Operate_Time = #{employeeOperateTime} 

  		   and Employee_ID = #{employeeId} 
  		   and Employee_Name like #{employeeName} 
  		   and Employee_Dept like #{employeeDept} 
  		   and b.name like #{employeeOperatorName}
