mapper.select:
		select `Record_ID`,
		a.`Customer_ID`,b.`Customer_Name` as `Record_Customer_Name`,
		a.`Business_ID`,c.`Business_Name` as `Record_Business_Name`,
		`Record_Name`,`Record_Phase`,`Record_Sale_Opportunity`,`Record_Expense_Category`,`Record_Expense_Actual`,
		a.`Record_Executor`, d.`Employee_Name` as `Record_Executor_Name`,
		`Record_Begin_Date`,`Record_End_Date`,`Record_Execute_Status`,`Record_Attachment`,`Record_Remarks`,`Record_Operator`,`Record_Operate_Time`,
		a.`Record_Creator`,f.`name`,
		`Record_Create_Time`,
        a.`Project_ID`,e.`Project_Name` as `Record_Project_Name` 
		from sales_record a
		left join
        development_project e on a.`Project_ID`=e.`Project_ID`
		left join sales_company_customer b on a.`Customer_ID`=b.`Customer_ID`
		left join sales_business c on a.`Business_ID`=c.`Business_ID`
		left join inner_org_employee d on a.`Record_Executor`=d.`Employee_ID`
		left join sys_user f on a.`Record_Creator`=f.`user_id`

		  		   and Record_ID = #{recordId} 
		  		   and Record_Name = #{recordName} 
		  		   and Record_Phase = #{recordPhase} 
		  		   and Record_Sale_Opportunity = #{recordSaleOpportunity} 
		  		   and Record_Expense_Category = #{recordExpenseCategory} 
		  		   and Record_Expense_Actual = #{recordExpenseActual} 
		  		   and Record_Begin_Date = #{recordBeginDate} 
		  		   and Record_End_Date = #{recordEndDate} 
		  		   and Record_Execute_Status = #{recordExecuteStatus} 
		  		   and Record_Attachment = #{recordAttachment} 
		  		   and Record_Remarks = #{recordRemarks} 
		  		   and Record_Operator = #{recordOperator} 
		  		   and Record_Operate_Time = #{recordOperateTime} 
		  		   and Record_Creator = #{recordCreator} 
		  		   and Record_Create_Time = #{recordCreateTime} 

		  		   and a.Customer_ID = #{customerId} 
		  		   and a.Business_ID = #{businessId} 
		  		   and a.Project_ID = #{projectId}
		  		   and d.`Employee_Name` LIKE #{recordExecutor} 



                order by ${sort} ${order}


                order by Record_ID desc



			limit #{offset}, #{limit}

		select count(*) 
		from sales_record a
		left join
        development_project e on a.`Project_ID`=e.`Project_ID`
		left join sales_company_customer b on a.`Customer_ID`=b.`Customer_ID`
		left join sales_business c on a.`Business_ID`=c.`Business_ID`
		left join inner_org_employee d on a.`Record_Executor`=d.`Employee_ID`
		left join sys_user f on a.`Record_Creator`=f.`user_id`

		  		   and Record_ID = #{recordId} 
		  		   and Record_Name = #{recordName} 
		  		   and Record_Phase = #{recordPhase} 
		  		   and Record_Sale_Opportunity = #{recordSaleOpportunity} 
		  		   and Record_Expense_Category = #{recordExpenseCategory} 
		  		   and Record_Expense_Actual = #{recordExpenseActual} 
		  		   and Record_Begin_Date = #{recordBeginDate} 
		  		   and Record_End_Date = #{recordEndDate} 
		  		   and Record_Execute_Status = #{recordExecuteStatus} 
		  		   and Record_Attachment = #{recordAttachment} 
		  		   and Record_Remarks = #{recordRemarks} 
		  		   and Record_Operator = #{recordOperator} 
		  		   and Record_Operate_Time = #{recordOperateTime} 
		  		   and Record_Creator = #{recordCreator} 
		  		   and Record_Create_Time = #{recordCreateTime} 

		  		   and a.Customer_ID = #{customerId} 
		  		   and a.Business_ID = #{businessId} 
		  		   and a.Project_ID = #{projectId}
		  		   and d.`Employee_Name` LIKE #{recordExecutor}
