mapper.select:
select
`Complaint_ID`,`Customer_ID`,`Complaint_Project_Type`,`Complaint_Product_Category`,
`Complaint_Product_Name`,`Complaint_Product_Price`,`Complaint_Product_Description`,`Complaint_Company_Name`,`Complaint_Attachment`,`Complaint_Remarks`,
s.name AS `Complaint_Operator`,`Complaint_Operate_Time`,`Complaint_Create_Time`
from sales_competitor a
left join sys_user s on a.Complaint_Operator=s.user_id

	 and Complaint_ID = #{complaintId} 
	 and Customer_ID = #{customerId} 
	 and Complaint_Project_Type = #{complaintProjectType}

	 and Complaint_Product_Category = #{complaintProductCategory}

	 and Complaint_Product_Name = #{complaintProductName}

	 and Complaint_Product_Price = #{complaintProductPrice}

	 and Complaint_Product_Description =
		#{complaintProductDescription} 
	 and Complaint_Company_Name = #{complaintCompanyName}

	 and Complaint_Attachment = #{complaintAttachment} 
	 and Complaint_Remarks = #{complaintRemarks} 
	 and Complaint_Operator = #{complaintOperator} 
	 and Complaint_Operate_Time = #{complaintOperateTime}

	 and Complaint_Create_Time = #{complaintCreateTime} 



		order by ${sort} ${order}


		order by Complaint_ID desc



	limit #{offset}, #{limit}

select count(*) from sales_competitor a 
left join sys_user s on a.Complaint_Operator=s.user_id

	 and Complaint_ID = #{complaintId} 
	 and Customer_ID = #{customerId} 
	 and Complaint_Project_Type = #{complaintProjectType}

	 and Complaint_Product_Category = #{complaintProductCategory}

	 and Complaint_Product_Name = #{complaintProductName}

	 and Complaint_Product_Price = #{complaintProductPrice}

	 and Complaint_Product_Description =
		#{complaintProductDescription} 
	 and Complaint_Company_Name = #{complaintCompanyName}

	 and Complaint_Attachment = #{complaintAttachment} 
	 and Complaint_Remarks = #{complaintRemarks} 
	 and Complaint_Operator = #{complaintOperator} 
	 and Complaint_Operate_Time = #{complaintOperateTime}

	 and Complaint_Create_Time = #{complaintCreateTime}

mapper.update:
update sales_competitor a
a.Complaint_Operate_Time=now(), `Customer_ID` = #{customerId}, `Complaint_Project_Type` = #{complaintProjectType}, `Complaint_Product_Category` = #{complaintProductCategory}, `Complaint_Product_Name` = #{complaintProductName}, `Complaint_Product_Price` = #{complaintProductPrice},
`Complaint_Product_Description` = #{complaintProductDescription}, `Complaint_Company_Name` = #{complaintCompanyName}, `Complaint_Attachment` = #{complaintAttachment}, `Complaint_Remarks` = #{complaintRemarks}, `Complaint_Operator` = #{complaintOperator}, `Complaint_Operate_Time` = #{complaintOperateTime}, `Complaint_Create_Time` = #{complaintCreateTime}
where Complaint_ID = #{complaintId}
